+  );
+
+  // error response
+  fetchMock.post(
+    'end:/api/v1/chart/data?form_data=%7B%22slice_id%22%3A169%7D',
+    400,
+  );
+
+  // multiple results pane
+  fetchMock.post(
+    'end:/api/v1/chart/data?form_data=%7B%22slice_id%22%3A196%7D',
+    {
+      result: [
+        {
+          data: [
